sp_widget.client_script:
function($scope)
{
	var c=this;
	c.getData = function() 
	{

		c.server.get().then(
			function(response)
			{  
				c.data.tableData= angular.fromJson(response.data.tableData);
				 $scope.$emit("dataReady", c.data.tableData);
			//$scope.$broadcast("dataReady", c.data.tableData)
			})
	};
	c.getData();

	//var jsonListString=c.data.tableData.toString(); 	
	//sessionStorage.setItem('jsonListString', jsonListString);
}

