var notes=current.r_note;
var fqdns;
var pattern = /((http:\/\/)|(https:\/\/))*([a-zA-Z0-9-\_\.]+\.){1,5}[^\s]*/gi;
while ((fqdns = pattern.exec(notes)) !== null) 
{
	// This is necessary to avoid infinite loops with zero-width matches
	if (fqdns.index === pattern.lastIndex) 
	{
		pattern.lastIndex++;
	}
	// The result can be accessed through the `m`-variable.
	for(var i in fqdns)
	{
		if(i==0)
			{
				workflow.scratchpad.fqdn=fqdns[i];
				if(workflow.scratchpad.fqdn !=null)
				{
					workflow.scratchpad.indicatorId='';
					var gdt = new GlideDateTime();

					//Create record in Potential Indicators
					var gr=new GlideRecord('x_19668_halo_potentialindicators');
					gr.initialize();
					gr.incident_id=workflow.scratchpad.incidentId;
					gr.indicator_status='New';
					gr.indicator_type=findSysIdOfIndicatorType('Full Qualified Domain Name (FQDN)');
					gr.indicator_data=workflow.scratchpad.fqdn;
					gr.enrich_start_timestamp=gdt;
					gr.notes='';
					workflow.scratchpad.indicatorId=gr.insert();
					workflow.scratchpad.indicatorGr=gr;
				}
			}
			
	}//End of for
	
}


function findSysIdOfIndicatorType(string)
{
	var gr1=new GlideRecord('x_19668_halo_indicator_type');
	gr1.addQuery('name',string);
	gr1.query();
	if(gr1.next())
		{
			return gr1.sys_id;
		}
	return '';
}

