var pattern = /\b[a-fA-F0-9]{64}\b/;
var notes=current.r_note;
workflow.scratchpad.hashCode= pattern.exec(notes);
if(workflow.scratchpad.hashCode == null)
	{
		pattern = /\b[a-fA-F0-9]{40}\b/;
		workflow.scratchpad.hashCode= pattern.exec(notes);
		if(workflow.scratchpad.hashCode == null)
		{
			pattern = /\b[a-fA-F0-9]{32}\b/;
			workflow.scratchpad.hashCode= pattern.exec(notes);
		}
	}

workflow.scratchpad.incidentId=workflow.scratchpad.incidentId;
workflow.scratchpad.indicatorId='';
var gdt = new GlideDateTime();

if(workflow.scratchpad.hashCode !=null)
{
	//Create record in Potential Indicators
	var gr=new GlideRecord('x_19668_halo_potentialindicators');
	gr.initialize();
	gr.incident_id=workflow.scratchpad.incidentId;
	gr.indicator_status='New';
	gr.indicator_type=findSysIdOfIndicatorType('Hash Code');
	gr.indicator_data=workflow.scratchpad.hashCode+'';
	gr.enrich_start_timestamp=gdt;
	gr.notes='';
	workflow.scratchpad.indicatorId=gr.insert();
	workflow.scratchpad.indicatorGr=gr;
}
