answer=isIpaddressSelected();

function isIpaddressSelected()
{
	for(var i in workflow.scratchpad.selectIndicators)
	{
		var gr=new GlideRecord('x_19668_halo_indicator_type');
		gr.addQuery('sys_id',workflow.scratchpad.selectIndicators[i]);
		gr.query();
		if(gr.next())
			{
				if(gr.name == 'Full Qualified Domain Name (FQDN)')
					return 'yes';
			}
	}
	
	return 'no';
}

