 function Client(clientId,clientVersion)
{
	this.clientId = clientId;
	this.clientVersion = clientVersion;
}

function Threatinfo(threatTypes,platformTypes,threatEntryTypes,threatEntries)
{
	this. threatTypes =threatTypes;
	this. platformTypes =platformTypes;
	this. threatEntryTypes =threatEntryTypes;
	this. threatEntries =new ThreatEntries(threatEntries);
}

function ThreatEntries(threatEntries)
{
		this.url=threatEntries;
}
  
