try
{
	var result=workflow.scratchpad.googleSafeSearchOuput;
	var gdt = new GlideDateTime();
	var flag=0;
	if(result.matches.length >0 )
		for(var i=0;i< result.matches.length;i++)
		{
		if(result.matches[i].threatType == 'MALWARE' || result.matches[i].threatType == 'SOCIAL_ENGINEERING' || result.matches[i].threatType == 'THREAT_TYPE_UNSPECIFIED' || result.matches[i].threatType == 'UNWANTED_SOFTWARE'|| result.matches[i].threatType == 'POTENTIALLY_HARMFUL_APPLICATION')
		{
			flag=1;
			break;
		}
	}
	
	if(flag==1)
	{
		var gr=new GlideRecord('x_19668_halo_indicatorenrichment');
		gr.initialize();
		gr.indicator_id=workflow.scratchpad.indicatorId;
		gr.enrichment_status='Processed';
		gr.enrichment_data=workflow.scratchpad.googleEnrichmentData;
		gr.enrichmnet_impactlevel_ts=gdt.getLocalTime();
		gr.enrichment_impactlevel='High';
		gr.enrichment_value=++gr.enrichment_value;
		workflow.scratchpad.indicatorEnrichmentId=gr.insert();
		
		current.enrichment_count=++current.enrichment_count;
		
	}
}

catch(Exception)
{
	
}

