var json=new global.JSON();
var ranking='';

var gr =new GlideRecord('x_19668_halo_potentialindicators');
gr.addQuery('incident_id',workflow.scratchpad.incidentId);
gr.addQuery('overall_urgency_ranking','=','LOW');
gr.query();   
if (gr.next()) 
{
	ranking='LOW';
	workflow.scratchpad.coaName='Found minor harmful indicators, escalate to next level for further analysis';
	workflow.scratchpad.ticketCoaReasoning='Reported Incident has no or minor harmful indicators ';
}


gr = new GlideRecord('x_19668_halo_potentialindicators');
gr.addQuery('incident_id',workflow.scratchpad.incidentId);
gr.addQuery('overall_urgency_ranking','=','MODERATE');
gr.query();   
if (gr.next()) 
{
	ranking='MODERATE';
	workflow.scratchpad.coaName='Found major harmful indicators, report incident threat level to FLARE and other relevant agencies';
	workflow.scratchpad.ticketCoaReasoning='Reported Incident has minor harmful indicators ';
}


