workflow.scratchpad.sysId='';
workflow.scratchpad.coaId='';
var gr=new GlideRecord('x_19668_halo_courseofactions');
gr.addQuery('ticket_coa_name',workflow.scratchpad.coaName);
gr.query();
while(gr.next())
{
	workflow.scratchpad.sysId=gr.sys_id+'';
}
gr=new GlideRecord('x_19668_halo_selcted_ticket_course_of_action');
gr.addQuery('incident_id',workflow.scratchpad.incidentId);
gr.addQuery('ticket_coa_selected_id',workflow.scratchpad.sysId);
gr.query();
if(gr.next())
{
	workflow.scratchpad.coaId=gr.sys_id;
}

else
{
	gr.initialize();
	gr.incident_id=workflow.scratchpad.incidentId;
	gr.ticket_coa_selected_id=workflow.scratchpad.sysId;
	gr.ticket_coa_reasoning=workflow.scratchpad.ticketCoaReasoning;
	gr.ticket_coa_recommended_id=workflow.scratchpad.sysId;
	workflow.scratchpad.coaId=gr.insert();
}


gr=new GlideRecord('x_19668_halo_incident');
gr.addQuery('sys_id',workflow.scratchpad.incidentId);
gr.query();
gr.next();
if(gr.ticket_coa =='')
	{
		gr.ticket_coa=workflow.scratchpad.coaId;
		gr.ticket_status='Ready for Review';
		gr.update();
	}

