  answer = ifScript();

function ifScript()
{
	gs.info("workflow.scratchpad.indicatorsCount :  "+workflow.scratchpad.indicatorsCount);
	gs.info("workflow.scratchpad.counter :  "+workflow.scratchpad.counter);
	if (workflow.scratchpad.indicatorsCount == workflow.scratchpad.counter)
	{
		return 'yes';
	}
	return 'no';
}

