workflow.scratchpad.indicatorId=indicatorsArray[workflow.scratchpad.counter];
workflow.scratchpad.counter=workflow.scratchpad.counter+1;

var uuidGenerator=new UUIDGenerator();
workflow.scratchpad.directory='/tmp/halo';
workflow.scratchpad.targetHost='192.168.6.82';
workflow.scratchpad.filename='StixFile'+workflow.scratchpad.counter+'.xml';
workflow.scratchpad.credentialsTag='flare_credentials';

//Get Glide Object
var gr=new GlideRecord('x_19668_halo_potentialindicators');
gr.addQuery('sys_id',workflow.scratchpad.indicatorId);
gr.query();
			
//Get Enrichment Data
workflow.scratchpad.indicatorDescription='';
var gr1=new GlideRecord('x_19668_halo_indicatorenrichment');
gr1.addQuery('indicator_id',workflow.scratchpad.indicatorId);
gr1.query();
while(gr.next())
	{
		workflow.scratchpad.indicatorDescription+=gr1.enrichment_data+"\n";
	}

workflow.scratchpad.incidentDescription=" Indicators: Halo Indicator: "+gr.indicator_id+" ,  part of Incident: "+current.incident_id;
workflow.scratchpad.indicatorCreatedTime=gr.sys_created_on+'';
