workflow.scratchpad.indicatorId="example:indicator-"+uuidGenerator.getUuid();
workflow.scratchpad.indicatorType=gr.indicator_type.name+'';
workflow.scratchpad.indicatorObservableId="example:Observable-"+uuidGenerator.getUuid();
workflow.scratchpad.indicatorCyboxId="example:Address-"+uuidGenerator.getUuid();
workflow.scratchpad.addressCategory="ipv4-addr";
workflow.scratchpad.addressValue=gr.indicator_data+'';
workflow.scratchpad.ttpId="example:ttp-"+uuidGenerator.getUuid();
workflow.scratchpad.ttpCreatedDate=gr.sys_created_on+'';
